char* data = "<!DOCTYPE html>\n<html>\n<head>\n<meta charset=\"utf-8\">\n<meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n<title>ClangServer</title>\n<style>\ndiv{\n    background-color: #fff;\n    border-radius: 20px;\n    width: 320px;\n    height: 350px;\n    margin: 0 auto;\n    padding: 20px;\n    text-align: left;\n}\na{\n    background-color: #fff;\n    border-radius: 10px;\n    width: 60px;\n    height: 40px;\n    margin: 8px;\n    padding-left: 20px;\n    padding-right: 20px;\n    padding-top: 10px;\n    padding-bottom: 10px;\n    cursor:pointer;\n}\n\n.btn1{\n	background-color:DodgerBlue;\n}\n\n.btn2{\n	background-color:Peru;\n}\n\n.btn3{\n	background-color:LightSeaGreen;\n}\n\n.btn4{\n	background-color:OrangeRed;\n}\n\n.btn1:active, .btn2:active, .btn3:active, .btn4:active{\n    background-color: #999;\n}\n</style>\n</head>\n<body style=\"text-align: center;background-color: #666;\">\n<br/>\n<div id=\"myDiv\">你好。这是一个 DIV 元素。</div>\n<br/>\n<a class=\"btn1\" onclick=\"event1()\">Click</a>\n<a class=\"btn2\" onclick=\"event2()\">Click</a>\n<a class=\"btn3\" onclick=\"event3()\">Click</a>\n<a class=\"btn4\" onclick=\"event4()\">Click</a>\n<script>\nfunction event1(){\n    send(\"/1\");\n}\nfunction event2(){\n    send(\"/2\");\n}\nfunction event3(){\n    send(\"/3\");\n}\nfunction event4(){\n    send(\"/4\");\n}\n\nfunction send(url){\n    var xmlhttp;\n	if (window.XMLHttpRequest){\n		xmlhttp=new XMLHttpRequest();\n	} else {\n		xmlhttp=new ActiveXObject(\"Microsoft.XMLHTTP\");\n	}\n	xmlhttp.onreadystatechange = function(){\n		if (xmlhttp.readyState==4 && xmlhttp.status==200){\n			var data = JSON.parse(xmlhttp.responseText);\n			document.getElementById(\"myDiv\").innerHTML=data.data;\n		}\n	};\n	xmlhttp.open(\"GET\", url, true);\n	xmlhttp.send();\n}\n</script>\n</body>\n</html>\n";